mapper.delete:
DELETE FROM TBL_BOARD_LIKE
 WHERE BNO = #{bno} 
   AND USER_ID = #{userid}

mapper.update:

        UPDATE TBL_BOARD 
		   SET LIKES = (SELECT COUNT(*) FROM TBL_BOARD_LIKE WHERE BNO = #{bno}) 
		 WHERE BNO = #{bno}

mapper.select:
SELECT COUNT(*) AS LIKE_CNT
  FROM TBL_BOARD_LIKE 
 WHERE BNO = #{bno}
   AND USER_ID = #{userid}
