mapper.update:
update TBL_REPLY_KNW_SHR set reply = #{reply}, updatedate =
sysdate where rno =
#{rno}

mapper.select:

select  rno, bno, reply, replyer, replydate, updatedate, selectedyn, likes
from 
  (
   select /*+INDEX(TBL_REPLY_KNW_SHR idx_reply) */ 
     rownum rn,  rno, bno, reply, replyer, replyDate, updatedate, selectedyn, likes
   from TBL_REPLY_KNW_SHR
   where bno =  #{bno}
   and rno > 0
   and rownum <= #{cri.pageNum} * #{cri.amount}
  ) where rn > (#{cri.pageNum} -1) * #{cri.amount}

SELECT COUNT(*) AS LIKE_CNT
  FROM TBL_REPLY_LIKE 
 WHERE RNO = #{rno}
   AND USER_ID = #{replyer}

mapper.delete:
DELETE FROM TBL_REPLY_LIKE
 WHERE RNO = #{rno} 
   AND USER_ID = #{replyer}

mapper.select:
SELECT LIKES
  FROM TBL_REPLY_KNW_SHR 
 WHERE RNO = #{rno}

mapper.update:
UPDATE TBL_REPLY_KNW_SHR 
   SET LIKES = LIKES + #{likes}
 WHERE RNO = #{rno}

UPDATE TBL_REPLY_KNW_SHR 
   SET SELECTEDYN = 'Y'
 WHERE RNO = #{rno}
